import { Button } from "@material-ui/core";
import React from "react";
import Style from "./Question.module.css";
const Question = ({
  questions,
  score,
  options,
  setScore,
  currQuestion,
  setCurrQuestion,
  correctAns,
}) => {
  return (
    <div className={Style["question"]}>
      <div className={Style["question__que"]}>
        <p>{questions && questions[currQuestion]?.question}</p>
      </div>
      <div className={Style["question__option"]}>
        {options && options.map((option) => <button key={option}>{option}</button>)}
      </div>
      <div className={Style["question__action"]}>
          <Button variant="contained" color="secondary" style={{marginRight : "10px"}}>Quit</Button>
          <Button variant="contained" color="primary">Next Question</Button>
      </div>
    </div>
  );
};

export default Question;
