import React from "react";
import Style from "./Question.module.css";
const Question = ({
  questions,
  score,
  options,
  setScore,
  currQuestion,
  setCurrQuestion,
  correctAns,
}) => {
  return (
    <div>
      <div>
          <p>{questions[currQuestion]}</p>
      </div>
      <div>
          {options.map((option)=><button>{option}</button>)}
      </div>
    </div>
  );
};

export default Question;
